mapper.select:
select
  user_id, user_name, iphone_no, user_pwd_open, user_pwd_close, status
from wyxjava.sys_user


        and user_id = #{userId}


        and user_name = #{userName}


        and iphone_no = #{iphoneNo}


        and user_pwd_open = #{userPwdOpen}


        and user_pwd_close = #{userPwdClose}


        and status = #{status}
